    private void btnIngresarDistribucionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIngresarDistribucionActionPerformed
        try{    
        int tiempo = Integer.valueOf(textTiempo.getText());
        int taza = Integer.valueOf(textTazaInteres.getText());
        int saldoInicial = Integer.valueOf(textSaldoInicial.getText());
        int saldoFinal = Integer.valueOf(textSaldoFinal.getText());
        double porcentaje = taza/100.0;
        double dividendo = (double)(saldoFinal*porcentaje);
        double divisor = Math.pow((1+porcentaje), tiempo)-1;
        DefaultTableModel modelFinal = (DefaultTableModel) tablaFinal.getModel();
        modelFinal.setRowCount(0);

        double pagoAnualyTaza = dividendo/divisor;
        double pagoAnual =  pagoAnualyTaza/(1+porcentaje);
        
        pagoAnualyTaza = Math.round(pagoAnualyTaza * 1000.0) / 1000.0;
        pagoAnual = Math.round(pagoAnual * 1000.0) / 1000.0;

        textPagoAnual.setText(String.valueOf(pagoAnual));
        textPagoTaza.setText(String.valueOf(pagoAnualyTaza));
        } catch (NumberFormatException e) {
        JOptionPane.showMessageDialog(this, "Ingrese valores numéricos válidos.", "Error", JOptionPane.ERROR_MESSAGE);
    }

    }//GEN-LAST:event_btnIngresarDistribucionActionPerformed

