    private void btnEvaluarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEvaluarActionPerformed
        DefaultTableModel model = (DefaultTableModel) tablaFinal.getModel();
    int rowCount = model.getRowCount();

    if (rowCount > 0) {
        // Obtener el último valor de la columna 2
        double ultimoValor = Double.parseDouble(model.getValueAt(rowCount - 1, 2).toString());

        // Obtener el saldo final ingresado en el textField
        double saldoFinal = Double.parseDouble(textSaldoFinal.getText());

        // Comparar y mostrar el mensaje en el textArea
        if (ultimoValor > saldoFinal) {
            areaConclusion.setText("Se tendrá más ganancia de lo esperado: " + ultimoValor + " > " + saldoFinal);
        } else {
            areaConclusion.setText("No se logró llegar al objetivo: " + ultimoValor + " < " + saldoFinal);
        }
    } else {
        areaConclusion.setText("No hay datos en la tabla para evaluar.");
    }
    }//GEN-LAST:event_btnEvaluarActionPerformed

