    private void btnCrearTablaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCrearTablaActionPerformed
        try {
        int tiempoAnios = Integer.parseInt(textTiempo.getText());
        double pagoAnual = Double.parseDouble(textPagoAnual.getText());
        double pagoAnualyTaza = Double.parseDouble(textPagoTaza.getText());
        double taza = Double.parseDouble(textTazaInteres.getText()); // Permite decimales
        double porcentaje = taza / 100.0;

        DefaultTableModel model = (DefaultTableModel) tablaFinal.getModel();
        
        // Inicializar la primera fila
        int numero = 1;
        model.addRow(new Object[]{numero, pagoAnual, pagoAnualyTaza});

        // Iterar para llenar la tabla
        while (numero < tiempoAnios) {  
            int rowCount = model.getRowCount();
            double anterior = ((Number) model.getValueAt(rowCount - 1, 2)).doubleValue(); // Conversión correcta

            double newPago = pagoAnual + anterior;
            double newPagoyTaza = newPago * (1 + porcentaje);

            // Redondeo correcto
            newPago = Math.round(newPago * 1000.0) / 1000.0;
            newPagoyTaza = Math.round(newPagoyTaza * 1000.0) / 1000.0;
            //if (numero == tiempoAnios-1) {
            //    newPagoyTaza = Math.ceil(newPagoyTaza);
            //}
            
            // Agregar nueva fila
            numero++;
            model.addRow(new Object[]{numero, newPago, newPagoyTaza});
        }
    } catch (NumberFormatException e) {
        JOptionPane.showMessageDialog(this, "Ingrese valores numéricos válidos.", "Error", JOptionPane.ERROR_MESSAGE);
    }
    }//GEN-LAST:event_btnCrearTablaActionPerformed

    private void btnGeneradorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGeneradorActionPerformed
        try {
            Random rand = new Random();

            // Generar valores aleatorios en los rangos indicados
            int tiempo = rand.nextInt(31) + 10; // Entre 10 y 40
            int tazaInteres = rand.nextInt(19) + 2; // Entre 2 y 20
            int saldoFinal = rand.nextInt(990001) + 10000; // Entre 10000 y 1000000

            // Asignar valores a los textFields
            textTiempo.setText(String.valueOf(tiempo));
            textTazaInteres.setText(String.valueOf(tazaInteres));
            textSaldoInicial.setText("0"); // Siempre 0
            textSaldoFinal.setText(String.valueOf(saldoFinal));
            
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Por favor, ingrese un número válido.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnGeneradorActionPerformed

