    private void btnGenerarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGenerarActionPerformed
        Random random = new Random();
    
    int incrementoVentas = random.nextInt(40 - 15 + 1) + 15; // Entre 15 y 40
    int incrementoVentasReduccion = random.nextInt(30 - 10 + 1) + 10; // Entre 10 y 30
    
    textIncrementoReembolso.setText(String.valueOf(incrementoVentas));
    textIncrementoReduccion.setText(String.valueOf(incrementoVentasReduccion));
    
    textVentasActuales.setText(String.valueOf(40000));
        textPrecioActual.setText(String.valueOf(45));
        textCostoVariable.setText(String.valueOf(5));

        textCantReembolso.setText(String.valueOf(10));
        textPorcentajeAprov.setText(String.valueOf(50));

        textReduccionPrecio.setText(String.valueOf(6));
        textPrecioActualReduccion.setText(String.valueOf(39));
    }//GEN-LAST:event_btnGenerarActionPerformed

    private void btnIngresarDatosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIngresarDatosActionPerformed
        try {
            int ventasActuales = Integer.valueOf(textVentasActuales.getText());
            int precioActual = Integer.valueOf(textPrecioActual.getText());
            int costoVariable = Integer.valueOf(textCostoVariable.getText());

            int cantReembolso = Integer.valueOf(textCantReembolso.getText());
            int incrementoVentas = Integer.valueOf(textIncrementoReembolso.getText());
            int cantAprovechado = Integer.valueOf(textPorcentajeAprov.getText());
            double porcentajeIncremento = incrementoVentas / 100.0;
            double porcentajeAprovechamiento = cantAprovechado / 100.0;
            //int minReembolso =  Integer.valueOf(textMinReembolso.getText());
            //int maxReembolso = Integer.valueOf(textMaxReembolso.getText());

            int reduccionPrecio = Integer.valueOf(textReduccionPrecio.getText());
            int precioReduccion = Integer.valueOf(textPrecioActualReduccion.getText());
            int incrementoVentasReduccion = Integer.valueOf(textIncrementoReduccion.getText());
            double porcentajeIncrementoReduccion = incrementoVentasReduccion / 100.0;
            //int minReduccion =  Integer.valueOf(textMinReduccion.getText());
            //int maxReduccion = Integer.valueOf(textMaxReduccion.getText());

            // utilidades
            double utilidadActual = (precioActual - costoVariable) * ventasActuales;

            // descuento
            //double precioConDescuento = ventasActuales*(1 + porcentajeIncremento) * (porcentajeAprovechamiento * (precioActual - cantReembolso) + (1-porcentajeAprovechamiento) * precioActual - costoVariable);
            double conDescuento = ventasActuales * (1 + porcentajeIncremento) * 
                ((porcentajeAprovechamiento * (precioActual - cantReembolso)) + 
                ((1 - porcentajeAprovechamiento) * precioActual) - costoVariable);
            

            // reducción de precio
            double precioConReduccion = precioReduccion - costoVariable;
            double conReduccion = ventasActuales * (1 + porcentajeIncrementoReduccion) * precioConReduccion;

            utilidadActual = Math.round(utilidadActual * 100.0) / 100.0;
            conDescuento = Math.round(conDescuento * 100.0) / 100.0;
            conReduccion = Math.round(conReduccion * 100.0) / 100.0;

            textUtilidadActual.setText(String.valueOf(utilidadActual));
            textConDescuento.setText(String.valueOf(conDescuento));
            textConReduccion.setText(String.valueOf(conReduccion));

        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Ingrese valores numéricos válidos.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnIngresarDatosActionPerformed

