    private void btnIngresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnIngresarActionPerformed
        try{
            int temperatura = Integer.valueOf(textTemperatura.getText());
            int tiempo = Integer.valueOf(textTiempo.getText());
            
            double x1 = 0.0;
            double x2 = 0.0;
            
            double campos = 0.0;
            
            x1 = (temperatura-125.0)/10.0;
            x2 = (tiempo-300.0)/30.0;
            
            campos = 90.79-(1.095*x1)-(1.045*x2)-(2.781*Math.pow(x1, 2))-(2.524*Math.pow(x2, 2))-(0.755*x1*x2);
            
            x1 = Math.round(x1 * 100.0) / 100.0;
            x2 = Math.round(x2 * 100.0) / 100.0;
            campos = Math.round(campos * 100.0) / 100.0;
            
            textTemperatura.setText(String.valueOf(temperatura));
            textTiempo.setText(String.valueOf(tiempo));
            textX1.setText(String.valueOf(x1));
            textX2.setText(String.valueOf(x2));
            textCampos.setText(String.valueOf(campos));
            
        } catch (NumberFormatException e) {
        JOptionPane.showMessageDialog(this, "Ingrese valores numéricos válidos.", "Error", JOptionPane.ERROR_MESSAGE);
    }

    }//GEN-LAST:event_btnIngresarActionPerformed

