    private void btnEvaluarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEvaluarActionPerformed
        try {
        DefaultTableModel model = (DefaultTableModel) tablaFinal.getModel();
        int rowCount = model.getRowCount();

        if (rowCount == 0) {
            JOptionPane.showMessageDialog(this, "La tabla está vacía.", "Error", JOptionPane.WARNING_MESSAGE);
            return;
        }

        double maxCampo = Double.MIN_VALUE;
        int maxRowIndex = -1;

        // Buscar el máximo en la última columna
        for (int i = 0; i < rowCount; i++) {
            double campo = Double.parseDouble(model.getValueAt(i, 4).toString());
            if (campo > maxCampo) {
                maxCampo = campo;
                maxRowIndex = i;
            }
        }

        // Obtener los valores de la fila donde se encontró el máximo
        int temperatura = Integer.parseInt(model.getValueAt(maxRowIndex, 0).toString());
        int tiempo = Integer.parseInt(model.getValueAt(maxRowIndex, 1).toString());
        double x1 = Double.parseDouble(model.getValueAt(maxRowIndex, 2).toString());
        double x2 = Double.parseDouble(model.getValueAt(maxRowIndex, 3).toString());

        // Formatear y mostrar el resultado en el área de texto
        String resultado = "La mejor manera de maximizar el ejercicio es con:\n" +
                           "Temperatura = " + temperatura + "\n" +
                           "Tiempo = " + tiempo + "\n" +
                           "x1 = " + x1 + "\n" +
                           "x2 = " + x2 + "\n" +
                           "Valor Maximizado = " + maxCampo;

        areaConclusion.setText(resultado);

    } catch (Exception e) {
        JOptionPane.showMessageDialog(this, "Ocurrió un error al evaluar la tabla.", "Error", JOptionPane.ERROR_MESSAGE);
    }
    }//GEN-LAST:event_btnEvaluarActionPerformed

    private void btnGenerarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGenerarActionPerformed
        try {
             Random random = new Random();

            int temperatura = random.nextInt(120 - 30 + 1) + 30; // Números entre 30 y 120
            int tiempo = random.nextInt(500 - 50 + 1) + 50; // Números entre 50 y 500

            // Asignar los valores a los JTextField
            textTemperatura.setText(String.valueOf(temperatura));
            textTiempo.setText(String.valueOf(tiempo));
            
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Por favor, ingrese un número válido.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnGenerarActionPerformed

