    private void btnCrearTablaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCrearTablaActionPerformed
        try {
            int temperatura = Integer.valueOf(textTemperatura.getText());
        int tiempo = Integer.valueOf(textTiempo.getText());

        double x1, x2, campos;

        int temperaturaMin = temperatura - 30;
        int temperaturaMax = temperatura + 30;
        int tiempoMin = tiempo - 50;
        int tiempoMax = tiempo + 50;

        DefaultTableModel model = (DefaultTableModel) tablaFinal.getModel();
        model.setRowCount(0); // Limpiar la tabla antes de llenarla

        // Recorrer los valores de tiempo
        for (int t = tiempoMin; t <= tiempoMax; t += 5) {
            // Recorrer los valores de temperatura
            for (int temp = temperaturaMin; temp <= temperaturaMax; temp += 5) {
                x1 = (temp - 125.0) / 10.0;
                x2 = (t - 300.0) / 30.0;

                campos = 90.79 - (1.095 * x1) - (1.045 * x2) - (2.781 * Math.pow(x1, 2)) - 
                         (2.524 * Math.pow(x2, 2)) - (0.755 * x1 * x2);

                // Redondear valores
                x1 = Math.round(x1 * 100.0) / 100.0;
                x2 = Math.round(x2 * 100.0) / 100.0;
                campos = Math.round(campos * 100.0) / 100.0;

                // Agregar fila a la tabla
                model.addRow(new Object[]{temp, t, x1, x2, campos});
            }
        }
        } catch (NumberFormatException e) {
            JOptionPane.showMessageDialog(this, "Ingrese valores numéricos válidos.", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_btnCrearTablaActionPerformed


