    private void btnCrearTablaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCrearTablaActionPerformed
        int filasSumar = 7; // Hasta la fila 7 (índice 6 en base 0)
        int filaResultado = 7; // Fila donde se colocará el resultado (índice 7 en base 0)
        int columnas = tablaFinal.getColumnCount();

        for (int col = 1; col < columnas; col += 2) { // Iterar solo columnas pares
            int suma = 0;
            for (int fila = 0; fila < filasSumar; fila++) {
                Object valor = tablaFinal.getValueAt(fila, col);
                if (valor != null) {
                    if (valor instanceof Number) {
                        suma += ((Number) valor).intValue();
                    } else {
                        try {
                            suma += Integer.parseInt(valor.toString().replace("$", "").replace(",", ""));
                        } catch (NumberFormatException e) {
                            // Ignorar valores no numéricos
                        }
                    }
                }
            }
            tablaFinal.setValueAt(suma, filaResultado, col); // Colocar suma en la fila 8
        }
    }//GEN-LAST:event_btnCrearTablaActionPerformed

