    private void btnGeneradorTotalesActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGeneradorTotalesActionPerformed
        int filaFuente = 7;  // Fila de donde tomamos los valores (índice 7 en base 0)
        int filaDestinoResta = 8;  // Fila donde colocaremos los resultados de la resta (índice 8 en base 0)
        int filaDestinoSuma = 9;   // Fila donde colocaremos los resultados de la suma (índice 9 en base 0)
        int filaDestinoRandom = 10; // Fila donde colocaremos el número aleatorio (índice 9 en base 0)
        int columnas = tablaFinal.getColumnCount();

        for (int col = 1; col < columnas; col += 2) { // Iterar solo columnas pares
            Object valorObj = tablaFinal.getValueAt(filaFuente, col);
            if (valorObj != null) {
                try {
                    double ultimoValor = Double.parseDouble(valorObj.toString().replace("$", "").replace(",", ""));
                    int valor = (int) ultimoValor;
                    int base;

                    if (valor >= 1_000_000) {
                        base = 100_000;
                    } else if (valor >= 100_000) {
                        base = 10_000;
                    } else if (valor >= 10_000) {
                        base = 1_000;
                    } else {
                        base = 100;
                    }

                    int maxRandom = Math.max(base, valor / 4);
                    int randomValue = (int) (Math.random() * (maxRandom - base + 1)) + base;

                    // Calcular resultados
                    int resultadoResta = valor - randomValue;
                    int resultadoSuma = valor + randomValue;

                    // Colocar los resultados en la tabla
                    tablaFinal.setValueAt(resultadoResta, filaDestinoResta, col);
                    tablaFinal.setValueAt(resultadoSuma, filaDestinoSuma, col);

                    // Generar número aleatorio entre los valores de la fila 8 y fila 9
                    int minRange = Math.min(resultadoResta, resultadoSuma);
                    int maxRange = Math.max(resultadoResta, resultadoSuma);
                    int randomBetween = (int) (Math.random() * (maxRange - minRange + 1)) + minRange;

                    // Colocar el número aleatorio en la fila 10
                    tablaFinal.setValueAt(randomBetween, filaDestinoRandom, col);
                    
                    llenarOtraTabla();

                } catch (NumberFormatException e) {
                    System.err.println("Error al convertir el valor en la columna " + col + ": " + e.getMessage());
                }
            }
        }
    }//GEN-LAST:event_btnGeneradorTotalesActionPerformed
    
    private void llenarOtraTabla(){
        int filaFuente = 10;  // Fila de donde tomamos los valores en tablaFinal (fila 10 en base 0)
        int filaDestinoInicial = 0; // Fila inicial en tablaTotales (fila 6 en base 0)
        int columnas = tablaFinal.getColumnCount();

        int filaDestinoTablaTotales = filaDestinoInicial; // Usamos una variable independiente

        // Paso 1: Transferir valores de tablaFinal (col. pares, fila 10) a tablaTotales (col. 2)
        for (int col = 1; col < columnas; col += 2) { 
            Object valorObj = tablaFinal.getValueAt(filaFuente, col);
            if (valorObj != null) {
                try {
                    int valor = Integer.parseInt(valorObj.toString().replace("$", "").replace(",", ""));

                    // Guardamos el valor en la fila correspondiente de tablaTotales
                    tablaTotales.setValueAt(valor, filaDestinoTablaTotales, 1);

                    // Incrementamos la fila destino para la siguiente iteración
                    filaDestinoTablaTotales++;

                } catch (NumberFormatException e) {
                    System.err.println("Error al convertir el valor en la columna " + col + ": " + e.getMessage());
                }
            }
        }

        // Paso 2: Sumar filas 1 a 6 en tablaTotales, columna 2 y colocarlo en la fila 7
        int sumaTotal = 0;
        for (int fila = 0; fila < 6; fila++) { 
            Object valorObj = tablaTotales.getValueAt(fila, 1);
            if (valorObj != null) {
                try {
                    sumaTotal += Integer.parseInt(valorObj.toString());
                } catch (NumberFormatException e) {
                    System.err.println("Error al sumar fila " + (fila + 1) + ": " + e.getMessage());
                }
            }
        }
        tablaTotales.setValueAt(sumaTotal, 6, 1);

        // Paso 3: Evaluar el valor de la fila 7 y calcular fila 8 (resta) y fila 9 (suma)
        int base;
        if (sumaTotal >= 1_000_000) {
            base = 100_000;
        } else if (sumaTotal >= 100_000) {
            base = 10_000;
        } else if (sumaTotal >= 10_000) {
            base = 1_000;
        } else {
            base = 100;
        }

        int maxRandom = Math.max(base, (int) sumaTotal / 4);
        int randomValue = (int) (Math.random() * (maxRandom - base + 1)) + base;

        int resultadoResta = sumaTotal - randomValue;
        int resultadoSuma = sumaTotal + randomValue;

        tablaTotales.setValueAt(resultadoResta, 7, 1); // Fila 8
        tablaTotales.setValueAt(resultadoSuma, 8, 1); // Fila 9

        // Paso 4: Generar número aleatorio entre fila 8 y fila 9, colocar en fila 10
        int minRange = (int) Math.min(resultadoResta, resultadoSuma);
        int maxRange = (int) Math.max(resultadoResta, resultadoSuma);
        int randomBetween = (int) (Math.random() * (maxRange - minRange + 1)) + minRange;

        tablaTotales.setValueAt(randomBetween, 9, 1); // Fila 10

        // Paso 5: Calcular textTotalFinal usando textContingencia y fila 10 de tablaTotales
        try {
            int valorFila10 = Integer.parseInt(tablaTotales.getValueAt(9, 1).toString());
            double porcentajeContingencia = Double.parseDouble(textContingencia.getText()) / 100.0;
            int totalFinal = valorFila10 + (int)(valorFila10 * porcentajeContingencia);

            textTotalFinal.setText("   Bs. " + totalFinal);
        } catch (NumberFormatException e) {
            System.err.println("Error al calcular el total final: " + e.getMessage());
        }
    }

